<script>
	
	export default {
		data() {
			return {
				timer: null,
				likeFlag: false,
				token: '',
				activity: {},
				showHeader: true,
				afterHeaderOpacity: 1, //不透明度
				headerPosition: 'fixed',
				headerTop: null,
				statusTop: null,
				nVueTitle: null,
				currentSwiper: 0,
				// 轮播图片
				swiperList: [],
				// 为您推荐列表
				categoryList: [],
				// Promotion: [],
				//本周热卖列表
				productList: [],
				loadingText: '正在加载...'
			};
		},
		
		onLoad() {
			
			// #ifdef APP-PLUS
			this.nVueTitle = uni.getSubNVueById('homeTitleNvue');
			this.nVueTitle.onMessage(res => {
				let type = res.data.type;
				if (type == 'focus') {
					this.toSearch();
				}
			});
			this.showHeader = false;
			this.statusHeight = plus.navigator.getStatusbarHeight();
			// #endif
			
		},
		beforeMount() {
			this.token = localStorage.getItem('token')
			this.renderSwiper()
			this.randerActivity()
			this.randergoods()
		},
		
		methods: {
			toActivity () {
				uni.navigateTo({
					url: '../../activity/activities?activityId=' + this.activity.id + '&invalid_time=' + this.activity.invalid_time
				})
			},
			// 添加至购物车
			addCar (goods) {
				this.$http.post('api/user/add/car', {
					token: this.token,
					goodsId: goods.goods_id,
					num: 1,
					isActive: '2'
				}).then(res=> {
					if (res.message == '添加成功') {
						uni.showTabBarRedDot({index:2});
					}
					uni.showToast({
						icon: 'none',
						title: res.message
					})
				})
			},
			// 收藏状态改变
			changeLike (id) {
				if (this.likeFlag) {
					uni.showToast({
						icon: 'none',
						title: '请稍后再试'
					})
					return
				}
				clearTimeout(this.timer)
				this.$http.post('api/user/goods/collect',{
					token: this.token,
					goodsID: id
				}).then(res=> {
					console.log(res)
					this.likeFlag = true
					this.timer = setTimeout(()=>{
						this.likeFlag = false
					},3000)
					if (res.message == 'OK') {
						this.randergoods()
					}
					uni.showToast({
						icon: 'none',
						title: res.message
					})
				},rej => {
					uni.showToast({
						icon: 'none',
						title: rej + '请稍后再试'
					})
				})
			},
			randergoods () {
				this.$http.post('api/home/class/goods',{
					token: this.token
				}).then(res=> {
					
					let product = res.data.find(ele=> ele.name == '本周热卖')
					let categoryList = res.data.find(ele=> ele.name == '为您推荐')
					
					this.productList = product.goods_list
					if (categoryList.goods_list.length > 4) {
						this.categoryList = categoryList.goods_list.splice(0,4)
					} else {
						this.categoryList = categoryList.goods_list
					}
				})
			},
			randerActivity () {
				this.$http.post('api/home/activity').then(res=> {
					this.activity = res.data[0]
				})
			},
			// 获取轮播图，渲染
			renderSwiper () {
				this.$http.post('api/home/banner').then(res=> {
					console.log(res)
					this.swiperList = res.data
				})
			},
			//消息列表
			toMsg() {
				uni.navigateTo({
					url: '../../msg/msg'
				})
			},
			//搜索跳转
			toSearch() {
				uni.navigateTo({
					url: '../../goods/goods-list/goods-list'
				})
			},
			//轮播图跳转
			toSwiper(e) {
				// uni.showToast({
				// 	title: e.src,
				// 	icon: 'none'
				// });
				if (e.url) {
					location.href = e.url
				}
			},
			//分类跳转
			toCategory(e) {
				//uni.showToast({title: e.name,icon:"none"});
				uni.setStorageSync('catName', e.name);
				uni.navigateTo({
					url: '../../goods/goods-list/goods-list?cid=' + e.id + '&name=' + e.name
				});
			},
			//推荐商品跳转
			toPromotion(e) {
				uni.showToast({
					title: e.title,
					icon: 'none'
				});
			},
			//商品跳转
			toGoods(e) {
				uni.showToast({
					title: '商品' + e.goods_id,
					icon: 'none'
				});
				uni.navigateTo({
					url: '../../goods/goods?goodsId=' + e.goods_id
				});
			},
			//轮播图指示器
			swiperChange(event) {
				this.currentSwiper = event.detail.current;
			}
		},
		onPageScroll(e) {
			//兼容iOS端下拉时顶部漂移
			this.headerPosition = e.scrollTop >= 0 ? "fixed" : "absolute";
			this.headerTop = e.scrollTop >= 0 ? null : 0;
			this.statusTop = e.scrollTop >= 0 ? null : -this.statusHeight + 'px';
		},
		//下拉刷新，需要自己在page.json文件中配置开启页面下拉刷新 "enablePullDownRefresh": true
		onPullDownRefresh() {
			setTimeout(function() {
				uni.stopPullDownRefresh();
			}, 1000);
		},
		//上拉加载，需要自己在page.json文件中配置"onReachBottomDistance"
		onReachBottom() {
			// uni.showToast({
			// 	title: '触发上拉加载'
			// });
			// let len = this.productList.length;
			// if (len >= 40) {
			// 	this.loadingText = '到底了';
			// 	return false;
			// }
			// // 演示,随机加入商品,生成环境请替换为ajax请求
			// let end_goods_id = this.productList[len - 1].goods_id;
			// for (let i = 1; i <= 10; i++) {
			// 	let goods_id = end_goods_id + i;
			// 	let p = {
			// 		goods_id: goods_id,
			// 		img: '/static/img/goods/p' + (goods_id % 10 == 0 ? 10 : goods_id % 10) + '.jpg',
			// 		name: '商品名称商品名称商品名称商品名称商品名称',
			// 		price: '￥168',
			// 		slogan: '1235人付款'
			// 	};
			// 	this.productList.push(p);
			// }
		}
